$(document).ready(() => {
  
  const button = $('#advice');
  const number = $('#number');
  const display = $('#display');

  button.click(async () => {

   const url = 'https://api.adviceslip.com/advice';
   const data = await $.getJSON(url);
   const { id, advice } = data.slip;

   display.text(advice);
   number.text(id);
   
  });
});