async function translate(message, language) {

  try {

 const response = await fetch('https://deep-translate1.p.rapidapi.com/language/translate/v2', {
   method: 'POST',
   headers: {
   'content-type': 'application/json',
   'X-RapidAPI-Key': '70e331a89dmsh4d6d4f93cb8bde1p188907jsnd1bbb52152b1',
   'X-RapidAPI-Host': 'deep-translate1.p.rapidapi.com',
   },
   body: JSON.stringify ({
     q: message,
     source: 'en',
     target: language
   })
 });

  const result = await response.json();
  return result.data.translations.translatedText;

  } catch (err) {
    console.error(err.message);
  };
}